    private void cargarTabla() {
        
        // Realizamos la modificación respectiva para que ni las filas ni las columnas sean editables
            
       
        DefaultTableModel tabla = new DefaultTableModel() {
            @Override
            public boolean isCellEditable(int row, int column) {
                return false;
            }
            
        };
                
        // Cargamos los nombres de las columnas
        
        String[] tablaColumnas = {"N° Auto", "Modelo", "Marca", "Motor", "Color", "Patente", "N° Puertas"};

        tabla.setColumnIdentifiers(tablaColumnas);
        
        // Setteamos el modelo detabla que usará el panel tblAutomovil
        
        tblAutomovil.setModel(tabla);
        
        // Cargamos en una lista un ArrayList<> que tenga los registros con los cuales recorrer con for
        
        ArrayList<Automovil> listaAutomoviles = controlador.encontrarLosAutomoviles();
         
        // Rellenamos la tabla con los registros traídos de la BBDD
        
        // Que la tabla no esté vacía, es decir tenga más de 0 registros
        if (tblAutomovil.getColumnCount() > 0) {
            // Recorremos cada elemento de nuestra lista de automóviles
            for (Automovil auto : listaAutomoviles) {
                // Armamos un vector de tipo Object[], ya que los elementos del registro pueden ser de diferente tipo
                
                Object[] registro = {auto.getAutoId(), auto.getModelo(), auto.getMarca(), auto.getMotor(),
                auto.getColor(), auto.getPatente(), auto.getCantidadPuertas()};
                
                // A cada registro completo, lo asignamos en la tabla (no la tabla panel, la tabla en memoria)
                
                tabla.addRow(registro);
                
            }
        }
        
        
    }
    
    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened

        cargarTabla();
        
    }//GEN-LAST:event_formWindowOpened

