    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        
        // Verificamos que la tabla (panel a la que está setteada) no se encuentre vacía 
        if (tblAutomovil.getRowCount() > 0) {
            
            // Verificamos que el elemento que seleccionemos EXISTA (-1 == no existe)
            if (tblAutomovil.getSelectedRow() != -1) {
                
                // Conseguimos el id del objeto registrado
                
                int numeroVehiculo = Integer.parseInt(String.valueOf(tblAutomovil.getValueAt(tblAutomovil.getSelectedRow(), 0)));
                
                // Configuramos las opciones para la eliminación del registro, con un vector de tipo Object[]
                
                Object[] opciones = {"Si", "No"};
                
                int resultadoOpcion = JOptionPane.showOptionDialog(
                null,
                "¿Está seguro de que desea borrar el automóvil seleccionado?",
                "Confirmación - Eliminación de Automóvil",
                JOptionPane.YES_NO_OPTION,
                JOptionPane.QUESTION_MESSAGE,
                null,
                opciones,
                opciones[0]
                );
                
                // Hacemos la validicación
                
                if (resultadoOpcion == JOptionPane.YES_OPTION) {
                    
                    // Le mandamos al controlador de la lógica el número obtenido para borrar
                    
                    Automovil vehiculoEncontrado = controlador.buscarAutomovil(numeroVehiculo);
                    
                    // Conseguimos el ID asociado del vehículo encontrado, hacemos todo estos pasos
                    // porque tenemos que corroborar que borremos el registro correcto y no lo que
                    // consigamos en la tabla, que es una representación gráfica
                    
                    int numeroAutomovilEncontrado = vehiculoEncontrado.getAutoId();
                    
                    // Mandamos un mensaje de eliminación antes de borrar
                    
                    JOptionPane.showMessageDialog(null, "Automóvil Eliminado: " + vehiculoEncontrado.toString());
                    
                    controlador.eliminarVehiculoSeleccionado(numeroAutomovilEncontrado);
                    
                    cargarTabla();
                    
                }
                
                
            } else {
                JOptionPane.showMessageDialog(null, "No se seleccionó ningún vehículo",
                        "Error", JOptionPane.ERROR_MESSAGE);
            }
        }
        
    }//GEN-LAST:event_btnEliminarActionPerformed

